import axios from 'axios';
export default {
  data() {
    return {
      userInput: '',
      shortenedUrl: '',
      apiResponse: null
    };
  },
  computed: {
    longUrl() {
      if (this.userInput.startsWith('http://') || this.userInput.startsWith('https://')) {
        return this.userInput;
      } else {
        return 'http://' + this.userInput;
      }
    }
  },
  methods: {
    async shortenUrl() {
      try {
        const response = await axios.post('http://127.0.0.1:8000/api/shorten', {
          longUrl: this.longUrl
        });
        this.shortenedUrl = response.data.short_url;
        this.apiResponse = response.data;
      } catch (error) {
        if (error.response && error.response.status === 422) {
          console.error('Validation Error:', error.response.data.errors);
        } else {
          console.error('Error shortening URL:', error);
        }
      }
    },
    async redirectToOriginal() {
      try {
        // Use axios to make a GET request to the shortened URL
        const response = await axios.get(`http://127.0.0.1:8000/api/${this.shortenedUrl}`);

        // If successful, redirect to the original URL
        window.location.href = response.request.responseURL;
      } catch (error) {
        console.error('Error redirecting to original URL:', error);
        // Handle errors, e.g., show an error message to the user
      }
    }
  }
};
